import React, { useContext, useEffect, useState } from 'react';
import ExpenseItem from "./ExpenseItem";
import { AppContext } from "../../context/AppContext";
import { Expense } from "../../types/types";
import { fetchExpenses, deleteExpense } from "../../utils/expense-utils"; 

const ExpenseList = () => {
  const { expenses = [], setExpenses } = useContext(AppContext); 
  const [loading, setLoading] = useState(true); // 로딩 상태 추가

  useEffect(() => {
    loadExpenses();
  }, []);

  const loadExpenses = async () => {
    try {
      const expenseList = await fetchExpenses();
      setExpenses(expenseList);
    } catch (err: any) {
      console.error("Error loading expenses:", err.message);
    } finally {
      setLoading(false); // 데이터 로딩 후 로딩 상태 해제
    }
  };

  const handleDelete = async (id: string) => {
    try {
      await deleteExpense(id); 
      setExpenses(expenses.filter((expense) => expense.id !== id)); 
    } catch (err: any) {
      console.error("Failed to delete expense:", err.message);
    }
  };

  return (
    <ul className="list-group">
      {loading ? ( // 로딩 중일 때 로딩 메시지 표시
        <p>Loading expenses...</p>
      ) : expenses.length === 0 ? ( // 로딩 완료 후 데이터가 없을 때 메시지 표시
        <p>No expenses to show.</p> 
      ) : ( // 로딩이 완료되고 데이터가 있을 때
        expenses.map((expense: Expense) => (
          <ExpenseItem
            key={expense.id}
            currentExpense={expense} 
            onDelete={() => handleDelete(expense.id)} 
          />
        ))
      )}
    </ul>
  );
};

export default ExpenseList;
