import React, { createContext, useState, useEffect } from "react";
import { Expense } from "../types/types";
import { fetchExpenses } from "../utils/expense-utils"; // 데이터 가져오기 함수

interface AppContextType {
  expenses: Expense[];
  setExpenses: React.Dispatch<React.SetStateAction<Expense[]>>;
  budget: number;
  setBudget: React.Dispatch<React.SetStateAction<number>>;
}

const initialState: AppContextType = {
  expenses: [],
  setExpenses: () => {},
  budget: 1000,
  setBudget: () => {},
};

export const AppContext = createContext<AppContextType>(initialState);

export const AppProvider: React.FC = ({ children }) => {
  const [expenses, setExpenses] = useState<Expense[]>(initialState.expenses);
  const [budget, setBudget] = useState<number>(initialState.budget);
  const [loading, setLoading] = useState(true); // 로딩 상태 추가

  useEffect(() => {
    const loadExpenses = async () => {
      try {
        const expenseList = await fetchExpenses();
        setExpenses(expenseList);
      } catch (error) {
        console.error("Failed to load expenses:", error);
      } finally {
        setLoading(false); // 데이터 로딩 후 로딩 상태 해제
      }
    };

    loadExpenses();
  }, []);

  return (
    <AppContext.Provider
      value={{
        expenses,
        setExpenses,
        budget,
        setBudget,
      }}
    >
      {loading ? <p>Loading expenses...</p> : children} {/* 로딩 상태일 때 메시지 표시 */}
    </AppContext.Provider>
  );
};
